mapper.select:
SELECT * FROM Table_chat_history LEFT JOIN Table_login_user ON Table_login_user.Id = Table_chat_history.loginUserId LEFT JOIN Table_elder_user ON Table_elder_user.elderUserId=Table_chat_history.elderUserId LEFT JOIN table_collection ON table_collection.chatId = Table_chat_history.chatId AND table_collection.elderUserId=#{elderUserId,jdbcType=INTEGER} WHERE Table_chat_history.elderUserId = #{elderUserId,jdbcType=INTEGER}
and (Table_chat_history.quest like concat('%',#{search},'%') or Table_chat_history.elderUserResponse like concat('%',#{search},'%'))
and Table_chat_history.chatid in (select chatid from table_collection where table_collection.elderUserId=#{elderUserId,jdbcType=INTEGER} )
and Table_chat_history.loginUserId=#{loginUserId,jdbcType=INTEGER}
ORDER BY Table_chat_history.chatId DESC
